import { Component, ElementRef, ViewChild } from '@angular/core';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css'],
})
export class HomePageComponent {
  iframeUrl = 'https://www.w3schools.com/html/';
  @ViewChild('myIframe')
  iframeRef!: ElementRef;

  toggleFullscreen() {
    const iframe: any = this.iframeRef.nativeElement;
    if (!document.fullscreenElement) {
      if (iframe.requestFullscreen) {
        iframe.requestFullscreen();
      } else if (iframe.mozRequestFullScreen) {
        /* Firefox */
        iframe.mozRequestFullScreen();
      } else if (iframe.webkitRequestFullscreen) {
        /* Chrome, Safari and Opera */
        iframe.webkitRequestFullscreen();
      } else if (iframe.msRequestFullscreen) {
        /* IE/Edge */
        iframe.msRequestFullscreen();
      }
    } else {
      if (document.exitFullscreen) {
        document.exitFullscreen();
      }
    }
  }
}
<div class="home_page">
  <div class="page1">
    <button class="button" (click)="toggleFullscreen()">Fullscreen</button>
    <!-- <button class="button">Zoom</button> -->
    <iframe
      class="iframe"
      src="https://www.bing.com"
      title="Trading View"
    ></iframe>
  </div>
  <div class="page2">
    <!-- <iframe [src]="iframeUrl"></iframe> -->
    <iframe
      class="iframe"
      src="https://www.tradingview.com"
      title="Trading View"
    ></iframe>
  </div>
</div>
<div class="home_page">
  <div class="page3">
    <iframe
      class="iframe"
      src="https://www.openstreetmap.org/export/embed.html?bbox=-0.004017949104309083%2C51.47612752641776%2C0.00030577182769775396%2C51.478569861898606&layer=mapnik"
      title="Trading View"
    ></iframe>
  </div>
  <div class="page4">
    <iframe
      class="iframe"
      src="https://en.wikipedia.org/wiki/HTML_element#Frames"
      title="Trading View"
    ></iframe>
  </div>
</div>
