import { DOCUMENT } from '@angular/common';
import {
  Component,
  Inject,
  Input,
  OnInit,
  QueryList,
  ViewChildren,
} from '@angular/core';

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css'],
})
export class HomePageComponent implements OnInit {
  // iframeUrl = 'https://www.w3schools.com/html/';

  @ViewChildren('myIframe1, myIframe2')
  iframes!: QueryList<any>;
  @Input() srcUrl = 'https://in.investing.com/indices/bank-nifty';
  @Input() iframeId = ''; // Unique ID for each iframe
  isFullscreen = false;
  activeIframe: any;

  constructor(@Inject(DOCUMENT) private document: any) {}
  elem: any;

  otherIframeUrls: string[] = [
    'https://www.bing.com',
    'https://in.investing.com/indices/s-p-cnx-nifty',
    // Add more URLs as needed
  ];

  ngOnInit(): void {
    this.elem = document.documentElement;
  }

  // openFullscreen() {
  //   if (this.elem.requestFullscreen) {
  //     this.elem.requestFullscreen();
  //   } else if (this.elem.mozRequestFullScreen) {
  //     /* Firefox */
  //     this.elem.mozRequestFullScreen();
  //   } else if (this.elem.webkitRequestFullscreen) {
  //     /* Chrome, Safari and Opera */
  //     this.elem.webkitRequestFullscreen();
  //   } else if (this.elem.msRequestFullscreen) {
  //     /* IE/Edge */
  //     this.elem.msRequestFullscreen();
  //   }
  // }

  toggleFullscreen() {
    this.isFullscreen = !this.isFullscreen;

    if (this.isFullscreen) {
      const iframeElement = this.getIframeElement(this.iframeId);
      if (iframeElement.requestFullscreen) {
        iframeElement.requestFullscreen();
      } else if (iframeElement.mozRequestFullScreen) {
        // Firefox
        iframeElement.mozRequestFullScreen();
      } else if (iframeElement.webkitRequestFullscreen) {
        // Chrome, Safari & Opera
        iframeElement.webkitRequestFullscreen();
      } else if (iframeElement.msRequestFullscreen) {
        // Edge
        iframeElement.msRequestFullscreen();
      }
    }
    // else {
    //   this.exitFullscreen();
    // }
  }

  // exitFullscreen() {
  //   if (document.exitFullscreen) {
  //     document.exitFullscreen();
  //   } else if (document.mozCancelFullScreen) {
  //     document.mozCancelFullScreen();
  //   } else if (document.webkitExitFullscreen) {
  //     document.webkitExitFullscreen();
  //   } else if (document.msExitFullscreen) {
  //     document.msExitFullscreen();
  //   }
  // }

  getIframeElement(iframeId: string): any {
    // Get the iframe element using the provided iframeId
    const iframe = this.iframes.find(
      (item) => item.nativeElement.id === iframeId
    );
    return iframe.nativeElement as any;
  }
}
<div class="home_page">
  <div class="page1">
    <div>
      <button class="button" (click)="toggleFullscreen()">
        Toggle Fullscreen
      </button>
    </div>
    <div class="iframe-container">
      <iframe
        #myIframe1
        src="https://www.bing.com"
        [ngClass]="{ fullscreen: isFullscreen && activeIframe === myIframe1 }"
      ></iframe>
    </div>
    <!-- <iframe
      class="iframe"
      src="https://www.bing.com"
      iframeId="iframe1"
      title="Trading View"
    >
    </iframe> -->
  </div>
  <div class="page2">
    <button class="button" (click)="toggleFullscreen()">
      Toggle Fullscreen
    </button>
    <div>
      <iframe
        #myIframe2
        *ngFor="let iframeUrl of otherIframeUrls"
        src="https://in.investing.com/indices/s-p-cnx-nifty"
        [ngClass]="{ fullscreen: isFullscreen && activeIframe === myIframe2 }"
      ></iframe>
    </div>
    <!-- <div>
      <button class="button" (click)="openFullscreen()">Full screen</button>
    </div> -->
    <!-- <iframe
      class="iframe"
      src="https://in.investing.com/indices/s-p-cnx-nifty"
      iframeId="iframe2"
      title="Trading View"
      #myIframe
      [ngClass]="{ fullscreen: isFullscreen }"
    >
    </iframe> -->
  </div>
</div>
<div class="home_page">
  <div class="page3">
    <iframe
      class="iframe"
      src="https://in.investing.com/equities/state-bank-of-india"
      title="Trading View"
    ></iframe>
  </div>
  <div class="page4">
    <iframe
      class="iframe"
      src="https://in.investing.com/indices/sensex"
      title="Trading View"
    ></iframe>
  </div>
</div>
