import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-iframe',
  templateUrl: './iframe.component.html',
  styleUrls: ['./iframe.component.css'],
  // template: `
  //   <form (ngSubmit)="submitForm()">
  //     <input [(ngModel)]="url1" />
  //     <input [(ngModel)]="url2" />
  //     <input [(ngModel)]="url3" />
  //     <input [(ngModel)]="url4" />
  //     <button type="submit">Submit</button>
  //   </form>
  // `,
})
export class IframeComponent {
  url1: string = '';
  url2: string = '';
  url3: string = '';
  url4: string = '';
  submitClicked: boolean = false;

  constructor(private router: Router) {}

  openIframes() {
    const urls = [this.url1, this.url2, this.url3, this.url4];
    this.router.navigate(['/home-page'], { state: { urls } });
    this.submitClicked = true;
  }

  // =================================================================================
  // @Output() submitClicked = new EventEmitter<string[]>();

  // url1 = '';
  // url2 = '';
  // url3 = '';
  // url4 = '';

  // submitForm() {
  //   const urls = [this.url1, this.url2, this.url3, this.url4];
  //   this.submitClicked.emit(urls);
  // }
}
<form class="input_form" *ngIf="!submitClicked" (submit)="openIframes()">
  <h1>Enter All 4 URL To Display The Trading</h1>
  <label clas="label_box" for="url1"></label>
  <input
    class="input1"
    type="text"
    id="url1"
    [(ngModel)]="url1"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url2"></label>
  <input
    class="input1"
    type="text"
    id="url2"
    [(ngModel)]="url2"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url3"></label>
  <input
    class="input1"
    type="text"
    id="url3"
    [(ngModel)]="url3"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url4"></label>
  <input
    class="input1"
    type="text"
    id="url4"
    [(ngModel)]="url4"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <button class="button" type="submit">Submit</button>
</form>

<app-home-page *ngIf="submitClicked"></app-home-page>

