<div class="iframe-container">
  <div class="iframe-wrapper" *ngFor="let iframe of iframes">
    <iframe
      src="https://in.investing.com/indices/sensex"
      [ngClass]="{ fullscreen: iframe.isFullScreen }"
    ></iframe>
    <button class="button" (click)="toggleFullScreen(iframe)">
      Toggle Full Screen
    </button>
  </div>
</div>
