<div class="iframe-container">
  <button class="button" (click)="maximizeIframe()" *ngIf="!iframeMaximized">
    Maximize Iframe
  </button>

  <div class="iframe-wrapper" *ngIf="iframeMaximized">
    <iframe [src]="currentIframeSrc" frameborder="0"></iframe>
    <button class="button" (click)="restoreIframe()">Restore Iframe</button>
  </div>

  <div class="sidebar">
    <div *ngFor="let iframe of sidebarIframes">
      <iframe [src]="iframe" frameborder="0"></iframe>
    </div>
  </div>
</div>
