  <!-- <div class="page3">
    <iframe
      class="iframe"
      src="https://in.investing.com/equities/state-bank-of-india"
      title="Trading View"
    ></iframe>
  </div> -->

  <div class="page2">
    <div class="container">
      <iframe
        *ngFor="let url of iframeUrls; let i = index"
        [src]="url"
        [ngClass]="{ maximized: i === selectedIframe }"
      ></iframe>
      <button
        class="button"
        *ngFor="let url of iframeUrls; let i = index"
        (click)="maximizeIframe(i)"
      >
        Maximize Iframe {{ i + 1 }}
      </button>
    </div>
  </div>

  <div class="page4">
    <iframe
      class="iframe"
      src="https://in.investing.com/indices/sensex"
      title="Trading View"
    ></iframe>
  </div>
</div>
