import { Component } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-iframe',
  templateUrl: './iframe.component.html',
  styleUrls: ['./iframe.component.css'],
})
export class IframeComponent {
  url1: string = '';
  url2: string = '';
  url3: string = '';
  url4: string = '';
  submitClicked: boolean = false;

  constructor(private router: Router) {}

  openIframes() {
    const urls = [this.url1, this.url2, this.url3, this.url4];
    this.router.navigate(['/iframe-display'], { state: { urls } });
    this.submitClicked = true;
  }
}
<h1>Homepage</h1>
<form class="input_form" *ngIf="!submitClicked" (submit)="openIframes()">
  <label for="url1">URL 1:</label>
  <input type="text" id="url1" [(ngModel)]="url1" required /><br />

  <label for="url2">URL 2:</label>
  <input type="text" id="url2" [(ngModel)]="url2" required /><br />

  <label for="url3">URL 3:</label>
  <input type="text" id="url3" [(ngModel)]="url3" required /><br />

  <label for="url4">URL 4:</label>
  <input type="text" id="url4" [(ngModel)]="url4" required /><br />

  <button type="submit">Open Iframes</button>
</form>

<app-home-page *ngIf="submitClicked"></app-home-page>
