import { DOCUMENT } from '@angular/common';
import {
  Component,
  Inject,
  Input,
  OnInit,
  QueryList,
  ViewChildren,
} from '@angular/core';
interface Iframe {
  url: string;
  isFullScreen: boolean;
}

@Component({
  selector: 'app-home-page',
  templateUrl: './home-page.component.html',
  styleUrls: ['./home-page.component.css'],
})
export class HomePageComponent implements OnInit {
  // iframeUrl = 'https://www.w3schools.com/html/';

  // @ViewChildren('myIframe1, myIframe2')
  // iframes!: QueryList<any>;
  // @Input() srcUrl = 'https://in.investing.com/indices/bank-nifty';
  // @Input() iframeId = ''; // Unique ID for each iframe
  // isFullscreen = false;
  // activeIframe: any;

  // constructor() {}

  ngOnInit(): void {}

  // toggleFullscreen() {
  //   this.isFullscreen = !this.isFullscreen;

  //   if (this.isFullscreen) {
  //     const iframeElement = this.getIframeElement(this.iframeId);
  //     if (iframeElement.requestFullscreen) {
  //       iframeElement.requestFullscreen();
  //     } else if (iframeElement.mozRequestFullScreen) {
  //       // Firefox
  //       iframeElement.mozRequestFullScreen();
  //     } else if (iframeElement.webkitRequestFullscreen) {
  //       // Chrome, Safari & Opera
  //       iframeElement.webkitRequestFullscreen();
  //     } else if (iframeElement.msRequestFullscreen) {
  //       // Edge
  //       iframeElement.msRequestFullscreen();
  //     }
  //   }
  // }

  // getIframeElement(iframeId: string): any {
  //   // Get the iframe element using the provided iframeId
  //   const iframe = this.iframes.find(
  //     (item) => item.nativeElement.id === iframeId
  //   );
  //   return iframe.nativeElement as any;
  // }
  // =======================================================1=====================================================

  iframes: Iframe[] = [
    { url: 'https://in.investing.com/indices/sensex', isFullScreen: false },
    { url: 'https://in.investing.com/indices/sensex', isFullScreen: false },
    { url: 'https://example.com/iframe3', isFullScreen: false },
    { url: 'https://example.com/iframe4', isFullScreen: false },
  ];

  toggleFullScreen(iframe: Iframe) {
    // Toggle fullscreen state for the selected iframe
    iframe.isFullScreen = !iframe.isFullScreen;

    // If the selected iframe is made fullscreen, deactivate fullscreen for other iframes
    if (iframe.isFullScreen) {
      this.iframes.forEach((i) => {
        if (i !== iframe) {
          i.isFullScreen = false;
        }
      });
    }
  }

  hasFullScreenIframe(): boolean {
    return this.iframes.some((iframe) => iframe.isFullScreen);
  }

  getFullScreenIframe(): Iframe | null {
    return this.iframes.find((iframe) => iframe.isFullScreen) || null;
  }

  getFullScreenIframeUrl(): string {
    const fullscreenIframe = this.getFullScreenIframe();
    return fullscreenIframe ? fullscreenIframe.url : '';
  }

  getSidebarIframes(): Iframe[] {
    return this.iframes.filter((iframe) => !iframe.isFullScreen);
  }
}
