<div class="iframe-container">
  <div class="fullscreen-container" *ngIf="hasFullScreenIframe()">
    <div class="fullscreen-iframe">
      <iframe [src]="getFullScreenIframeUrl()"></iframe>
      <button (click)="(toggleFullScreen)">Minimize</button>
    </div>
  </div>
  <div class="sidebar-container">
    <div class="iframe-wrapper" *ngFor="let iframe of getSidebarIframes()">
      <iframe
        [src]="iframe.url"
        [ngClass]="{ minimized: iframe.isFullScreen }"
      ></iframe>
      <button
        class="button"
        *ngIf="!iframe.isFullScreen"
        (click)="toggleFullScreen(iframe)"
      >
        Maximize
      </button>
    </div>
  </div>
</div>
