import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-iframe',
  templateUrl: './iframe.component.html',
  styleUrls: ['./iframe.component.css'],
  // template: `
  //   <form (ngSubmit)="submitForm()">
  //     <input [(ngModel)]="url1" />
  //     <input [(ngModel)]="url2" />
  //     <input [(ngModel)]="url3" />
  //     <input [(ngModel)]="url4" />
  //     <button type="submit">Submit</button>
  //   </form>
  // `,
})
export class IframeComponent {
  @Input() url: any = '';

  url1: string = '';
  url2: string = '';
  url3: string = '';
  url4: string = '';
  submitClicked: boolean = false;

  constructor(private router: Router) {}

  openIframes() {
    console.log(this.url1);
    const urls = [this.url1, this.url2, this.url3, this.url4];
    // this.router.navigate(['/home-page'], { state: { urls } });
    this.submitClicked = true;
  }

  // =================================================================================
  // @Output() submitEvent = new EventEmitter<string[]>();

  // submitUrls() {
  //   const urls = [this.url1, this.url2, this.url3, this.url4];
  //   this.submitEvent.emit(urls);
  // }

  // ==============================================================
  // loadUrl1() {
  //   this.Url1 = 'https://www.url1.com'; // Replace with your dynamic URL logic
  // }
}
<!-- <form class="input_form" *ngIf="!submitClicked" (submit)="openIframes()"> -->
<!-- <form class="input_form" *ngIf="!submitClicked"> -->
<div class="input_form">
  <h1>Trading View</h1>
  <label clas="label_box" for="url1">URL 1 <br /></label>
  <input
    class="input1"
    type="text"
    id="url1"
    [(ngModel)]="url1"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url2">URL 2<br /></label>
  <input
    class="input1"
    type="text"
    id="url2"
    [(ngModel)]="url2"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url3">URL 3<br /></label>
  <input
    class="input1"
    type="text"
    id="url3"
    [(ngModel)]="url3"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <label for="url4">URL 4<br /></label>
  <input
    class="input1"
    type="text"
    id="url4"
    [(ngModel)]="url4"
    required
    placeholder="Enter URL"
  /><br />
  <br />

  <button class="button" type="submit" (click)="openIframes()">Submit</button>

  <!-- <button (click)="submitUrls()">Submit</button> -->
  <!-- </form> -->
</div>

<app-home-page *ngIf="submitClicked"></app-home-page>

