  // ngOnInit(): void {}

  resizeIframes(clickedIndex: number) {
    const clickedWidth = this.iframeWidth[clickedIndex - 1];
    const clickedHeight = this.iframeHeight[clickedIndex - 1];

    // Update clicked iframe size
    this.iframeWidth[clickedIndex - 1] = clickedWidth + 50; // Example: increase width by 50px
    this.iframeHeight[clickedIndex - 1] = clickedHeight + 50; // Example: increase height by 50px

    // Decrease sizes of other iframes
    for (let i = 0; i < this.iframeWidth.length; i++) {
      if (i !== clickedIndex - 1) {
        this.iframeWidth[i] = 150; // Example: set width to 150px
        this.iframeHeight[i] = 100; // Example: set height to 100px
      }
    }
  }
